import { axiosAuth } from "./utils/axiosAuth";

const AddAnimal = (props) => {
    const [animal, setAnimal] = useState({
            name: "",
            sound: "",
            classification: {species: ""}
    })
    const handleChanges = (e) => {
        setAnimal({
                ...animal,
                [e.target.name]: e.target.value
        })
    }

    const handleSubmit = () => {
        e.preventDefault();
